# https://leetcode.com/problems/largest-magic-square/description/?envType=daily-question&envId=2026-01-17

from typing import List


class Solution:
    def largestMagicSquare(self, grid: List[List[int]]) -> int:
        def areElementsEqual(arr):
            return all(ele == arr[0] for ele in arr)
        
        def getMagicSquareSize(startRow, startCol, i, j, rowSum, colSum, diagonalSum):
            maxSize = 0
            if rowSum[-1] == colSum[-1] == diagonalSum[0] == diagonalSum[1] and areElementsEqual(rowSum) and areElementsEqual(colSum):
                maxSize = len(rowSum)

            i = i + 1
            j = j + 1
            if i < len(grid) and j < len(grid[i]):
                for k in range(len(rowSum)):
                    rowSum[k] += grid[startRow + k][j]
                rowSum.append(sum(grid[i][startCol:j+1]))

                for k in range(len(colSum)):
                    colSum[k] += grid[i][startCol + k]
                colSum.append(sum(grid[k][j] for k in range(startRow, i + 1)))

                diagonalSum[0] += grid[i][j]

                k = startRow
                l = j
                dSum = 0
                while k <= i and l >= 0:
                    dSum += grid[k][l]
                    k = k + 1
                    l = l - 1
                diagonalSum[1] = dSum

                return max(maxSize, getMagicSquareSize(startRow, startCol, i, j, rowSum, colSum, diagonalSum))
            return maxSize

        maxSize = 0
        for i in range(len(grid)):
            for j in range(len(grid[i])):
                maxSize = max(maxSize, getMagicSquareSize(
                    i, j, i, j, [grid[i][j]], [grid[i][j]], [grid[i][j], grid[i][j]]))

        return maxSize


